import * as THREE from 'three'
import styled from 'styled-components'
import { useState, useEffect, useRef } from 'react'
import { Vector2 } from 'three';
import { render } from 'react-dom';

const Collections3D = () => {

    const [targeted, setTargeted] = useState(false);
    const mountScene: {current: any} = useRef();

    useEffect(() => {

        let scene: any, camera: any, renderer: any;
        let width = 500;
        let height = 500;

        function init() {
            scene = new THREE.Scene();
            camera = new THREE.OrthographicCamera( width / - 2, width / 2, height / 2, height / - 2, 1, 1000 );
            scene.add( camera );
            camera.position.z = 5;

            renderer = new THREE.WebGLRenderer({antialias: true, alpha: true});
            renderer.setSize( width, height);
            mountScene.current.appendChild( renderer.domElement);

            renderer.setClearColor( 0xfffff, 0 ); // white background - replace ffffff with any hex color

            camera.position.z = 5;


            //CUBES
            let geoCubeCenter = new THREE.BoxGeometry(1,1, 1 );
            let geoCubeTopLeft = new THREE.BoxGeometry(1, 1, .001);
            let geoCubeBottomRight = new THREE.BoxGeometry(1, 1, .001);

            geoCubeTopLeft.translate(-4, 4, 0);
            geoCubeBottomRight.translate(4, -4, 0);


            let material = new THREE.MeshBasicMaterial( { color: targeted ? 0x000000 : 0xC8C8C8 } );
            let centerCube = new THREE.Mesh( geoCubeCenter, material );
            let topLeftCube = new THREE.Mesh( geoCubeTopLeft, material );
            let bottomRightCube = new THREE.Mesh( geoCubeBottomRight, material );
            scene.add( centerCube );
            scene.add( topLeftCube );
            scene.add( bottomRightCube );

            //SPHERES
            let geoSphereLeft = new THREE.CylinderGeometry(.65,.65, .001, 32);
            let sphereLeft = new THREE.Mesh( geoSphereLeft, material );
            geoSphereLeft.translate(-4, 0, .02);
            sphereLeft.rotation.x += 1.6;
            scene.add( sphereLeft );

            let geoSphereRight = new THREE.CylinderGeometry(.65,.65, .001, 32);
            let sphereRight = new THREE.Mesh( geoSphereRight, material );
            geoSphereRight.translate(4, 0, .02);
            sphereRight.rotation.x += -1.6;
            scene.add( sphereRight );

            let geoSphereTop = new THREE.CylinderGeometry(.65,.65, .001, 32);
            let sphereTop = new THREE.Mesh( geoSphereTop, material );
            geoSphereTop.translate(0, 0, 4);
            sphereTop.rotation.x += -1.6;
            scene.add( sphereTop );

            let geoSphereBottom = new THREE.CylinderGeometry(.65,.65, .001, 32);
            let sphereBottom = new THREE.Mesh( geoSphereBottom, material );
            geoSphereBottom.translate(0, 0, -4);
            sphereBottom.rotation.x += -1.6;
            scene.add( sphereBottom );

            //Triangles 
            let triangleTop = new THREE.Geometry();
            triangleTop.vertices.push(new THREE.Vector3(-.7,0,0.1));
            triangleTop.vertices.push(new THREE.Vector3(.7,0,0.1));
            triangleTop.vertices.push(new THREE.Vector3(0,1.3,0.1));
            triangleTop.faces.push(new THREE.Face3(0,1,2));
            var mat = new THREE.MeshBasicMaterial({color: 0xC8C8C8, side: THREE.DoubleSide})
            triangleTop.translate(3.9,3.2, 0);

            let mesh = new THREE.Mesh( triangleTop,mat);
            scene.add(mesh);

            let geoTriangleBottom = new THREE.Geometry();
            geoTriangleBottom.vertices.push(new THREE.Vector3(-.7,0,0.1));
            geoTriangleBottom.vertices.push(new THREE.Vector3(.7,0,0.1));
            geoTriangleBottom.vertices.push(new THREE.Vector3(0,1.3,0.1));
            geoTriangleBottom.faces.push(new THREE.Face3(0,1,2));
            var mat = new THREE.MeshBasicMaterial({color: 0xC8C8C8, side: THREE.DoubleSide})
            geoTriangleBottom.translate(-3.9,-4.6, 0);

            let triangleBottom = new THREE.Mesh( geoTriangleBottom,mat);
            scene.add(triangleBottom);

        }

        // let light = new THREE.DirectionalLight(0xffffff);
        // light.position.set(1, 0, 1).normalize(); // soft white light
        // scene.add( light );



        let raycaster = new THREE.Raycaster();
        let mouse = new THREE.Vector2();

        let animate = function () {
          requestAnimationFrame( animate );

          renderer.render( scene, camera );
        };

        init();
        animate();

        function render() {
            raycaster.setFromCamera( mouse, camera );

            // calculate objects intersecting the picking ray
            var intersects = raycaster.intersectObjects( scene.children );

            for ( var i = 0; i < intersects.length; i++ ) {

                console.log(intersects[i])

            }
            renderer.render(scene ,camera)
        }


        function onMouseMove( event: any ) {

            // calculate mouse position in normalized device coordinates
            // (-1 to +1) for both components

            mouse.x = ( event.clientX / window.innerWidth ) * 2 - 1;
            mouse.y = - ( event.clientY / window.innerHeight ) * 2 + 1;

        }


        window.addEventListener( 'mousemove', onMouseMove, false );




    },[targeted])


    return (
        <CollectionsStage  ref={mountScene}>
        </CollectionsStage>
    )
}

const CollectionsStage = styled.div`
    position: relative;
    top: 50%;
    transform: translatey(-50%);
    > canvas {
        max-height: 100px;
        max-width: 100px; 
    }

`

export default(Collections3D)
