import styled from 'styled-components'

interface Props {
    collection: {

    };
    onClick: (event: React.MouseEvent) => void;
    selected: boolean;
}

interface IProps {
    targeted: boolean;
    collections: {
        collections: [];
        current: string;
        open: boolean;
    };
};

const Collection: React.FC<Props> = ({collection, onClick, selected}) => {

    return (
        <CollectionContainer onClick={(event) => onClick(event)} selected={selected}>
        </CollectionContainer>
    );

}


const CollectionContainer = styled.li<Iprops>`
    list-style: none;
    width: 100px;
    height: 100px;
    background-color: black;
    margin: 40px;
    display: ${props => props.selected ? inline-block : none};
`

export default Collection
