import { createContext,useReducer } from 'react'
import { collectionsReducer } from '../reducers/collectionsReducer'

type collections = {
    name: string;
}

type InitType = {
    collections: collections[];
    current: string;
    open: boolean;
}


let initState = {
    collections: [],
    current: "init",
    open: false,
}

export const CollectionsContext = createContext<InitType>(initState, dispatch)

interface Props {
    children: React.ReactNode;
}


const CollectionsContextProvider: React.FC<Props> = (props) => {
    const [collectionsState, dispatch] = useReducer(collectionsReducer, initState)
     return(
        <CollectionsContext.Provider value={{collectionsState, dispatch}}>
        {props.children}
        </CollectionsContext.Provider>
    )
}


export default CollectionsContextProvider
