import * as THREE from 'three'
import styled from 'styled-components'
import { useEffect, useRef } from 'react'

const Collections3D = () => {

    const mountScene: {current: any} = useRef();

    useEffect(() => {
        var scene = new THREE.Scene();
        var camera = new THREE.PerspectiveCamera( 100, window.innerWidth/window.innerHeight, 0.1, 1000 );
        var renderer = new THREE.WebGLRenderer({antialias: true, alpha: true});
        renderer.setSize( 1000,  500);

        // document.body.appendChild( renderer.domElement );
        // use ref as a mount point of the Three.js scene instead of the document.body
        mountScene.current.appendChild( renderer.domElement );
        var geoCubeCenter = new THREE.BoxGeometry(1,1, 1 );
        var geoCubeTopLeft = new THREE.BoxGeometry(1, 1, 1);
        geoCubeTopLeft.translate(-2, 2, 0);

        var material = new THREE.MeshLambertMaterial( { color: 0xfffff } );
        var centerCube = new THREE.Mesh( geoCubeCenter, material );
        var topLeftCube = new THREE.Mesh( geoCubeTopLeft, material );
        scene.add( centerCube );
        scene.add( topLeftCube );
        topLeftCube.rotation.y += .21
        topLeftCube.rotation.x += .3

        var light = new THREE.DirectionalLight(0xffffff);
        light.position.set(1, 0, 1).normalize(); // soft white light
        scene.add( light );

        renderer.setClearColor( 0xffffff ); // white background - replace ffffff with any hex color

        camera.position.z = 5;
        var animate = function () {
          requestAnimationFrame( animate );

          renderer.render( scene, camera );
        };
        animate();
    },[])

    return (
        <CollectionsStage ref={mountScene}>
        </CollectionsStage>
    )
}

const CollectionsStage = styled.div`
    position: relative;
    top: 50%;
    transform: translatey(-50%);
    > canvas {
        max-height: 100px;
        max-width: 100px; 
    }

`

export default(Collections3D)
