import * as THREE from 'three'
import styled from 'styled-components'
import { useEffect, useRef } from 'react'

const Collections3D = () => {

    const mountScene: {current: any} = useRef();

    useEffect(() => {
        let scene = new THREE.Scene();
        let camera = new THREE.PerspectiveCamera( 100, 500/500, 0.1, 1000 );
        let renderer = new THREE.WebGLRenderer({antialias: true, alpha: true});
        renderer.setSize( 500,  500);
        mountScene.current.appendChild( renderer.domElement );
        //CUBES
        let geoCubeCenter = new THREE.BoxGeometry(1,1, 1 );
        let geoCubeTopLeft = new THREE.BoxGeometry(1, 1, .001);
        let geoCubeBottomRight = new THREE.BoxGeometry(1, 1, .001);

        geoCubeTopLeft.translate(-4, 4, 0);
        geoCubeBottomRight.translate(4, -4, 0);


        let material = new THREE.MeshLambertMaterial( { color: 0xC8C8C8 } );
        let centerCube = new THREE.Mesh( geoCubeCenter, material );
        let topLeftCube = new THREE.Mesh( geoCubeTopLeft, material );
        let bottomRightCube = new THREE.Mesh( geoCubeBottomRight, material );
        scene.add( centerCube );
        scene.add( topLeftCube );
        scene.add( bottomRightCube );

        //SPHERES
        let geoSphereLeft = new THREE.CylinderGeometry(.65,.65, .001, 32);
        let sphereLeft = new THREE.Mesh( geoSphereLeft, material );
        geoSphereLeft.translate(-4, 0, .02);
        sphereLeft.rotation.x += 1.6;
        scene.add( sphereLeft );

        let geoSphereRight = new THREE.CylinderGeometry(.65,.65, .001, 32);
        let sphereRight = new THREE.Mesh( geoSphereRight, material );
        geoSphereRight.translate(4, 0, .02);
        sphereRight.rotation.x += -1.6;
        scene.add( sphereRight );

        let geoSphereTop = new THREE.CylinderGeometry(.65,.65, .001, 32);
        let sphereTop = new THREE.Mesh( geoSphereTop, material );
        geoSphereTop.translate(0, 0, 4);
        sphereTop.rotation.x += -1.6;
        scene.add( sphereTop );

        let geoSphereBottom = new THREE.CylinderGeometry(.65,.65, .001, 32);
        let sphereBottom = new THREE.Mesh( geoSphereBottom, material );
        geoSphereBottom.translate(0, 0, -4);
        sphereBottom.rotation.x += -1.6;
        scene.add( sphereBottom );

        //Triangles 
        let triangleTop = new THREE.Geometry();
        triangleTop.vertices.push(new THREE.Vector3(-3,0,0.1));
        triangleTop.vertices.push(new THREE.Vector3(5,0,0.1));
        triangleTop.vertices.push(new THREE.Vector3(0,6,0.1));
        triangleTop.faces.push(new THREE.Face3(0,1,2));
        var mat = new THREE.MeshBasicMaterial({color: 0xFF0000, side: THREE.DoubleSide})

        let mesh = new THREE.Mesh( triangleTop,mat);
        scene.add(mesh);


        let light = new THREE.DirectionalLight(0xffffff);
        light.position.set(1, 0, 1).normalize(); // soft white light
        scene.add( light );

        renderer.setClearColor( 0xfffff, 0 ); // white background - replace ffffff with any hex color

        camera.position.z = 5;
        let animate = function () {
          requestAnimationFrame( animate );
          renderer.render( scene, camera );
        };
        animate();
    },[])

    return (
        <CollectionsStage ref={mountScene}>
        </CollectionsStage>
    )
}

const CollectionsStage = styled.div`
    position: relative;
    top: 50%;
    transform: translatey(-50%);
    > canvas {
        max-height: 100px;
        max-width: 100px; 
    }

`

export default(Collections3D)
